    this.setState({
      [e.target.id]:e.target.value

    })
    console.log(this.state)
  }
  handleSubmit=(e)=>{
    e.preventDefault
  }

  render()
  {
  return(
<div>
<form onSubmit={this.handleSubmit}>
<input type="text" placeholder="enter name..." id='name' onChange={this.handle}/>
<input type="number" placeholder="enter age..."id='name' onChange={this.handle} />

<input type="submit" value="add items"/>
</form>



</div>

    )
  }
}
